import { Toast } from "mint-ui";
export default {
  data: function data() {
    return {
      name: "",
      pwd: ""
    };
  },
  methods: {
    btnLogin: function btnLogin() {
      var regName = /^\w{3,8}$/;
      var regPwd = /^\d{3,8}$/;

      if (!regName.test(this.name)) {
        Toast("用户名有误,请重新输入");
        this.name = "";
        return;
      }

      if (!regPwd.test(this.pwd)) {
        Toast("密码有误，请重新输入");
        this.pwd = "";
        return;
      }

      var url = "http://127.0.0.1:3000/login?name=" + this.name + "&pwd=" + this.pwd;
      this.axios.get(url).then(function (result) {
        //console.log(result);
        if (result.data.code == 1) {
          Toast("登录成功");
        } else {
          Toast("用户名或密码有误");
        }
      });
    }
  }
